dbQuery:
db.movies.find(
  {},
  { title: 1, num_mflix_comments: 1, _id: 0 }
)
.sort({ num_mflix_comments: -1 })
.limit(10)

db.movies.find(
  {
    year: { $gte: 2015, $lte: 2017 }
  },
  {
    title: 1,
    year: 1,
    num_mflix_comments: 1,
    _id: 0
  }
).sort({ num_mflix_comments: -1 }).limit(15)

db.movies.aggregate([
  // Stage 1: Filter movies with more than 75 comments and valid IMDB ratings
  {
    $match: {
      num_mflix_comments: { $gt: 75 },
      "imdb.rating": { $exists: true, $ne: null }
    }
  },
  // Stage 2: Create rating ranges using $bucket
  {
    $bucket: {
      groupBy: "$imdb.rating",
      boundaries: [0, 2, 4, 6, 8, 10.1], // 0-2, 2-4, 4-6, 6-8, 8-10
      default: "Other",
      output: {
        count: { $sum: 1 },
        avg_comments: { $avg: "$num_mflix_comments" },
        movies: { $push: "$title" } // Optional: to see which movies fall in each range
      }
    }
  },
  // Stage 3: Format the output with readable rating ranges
  {
    $project: {
      rating_range: {
        $switch: {
          branches: [
            { case: { $eq: ["$_id", 0] }, then: "0-2" },
            { case: { $eq: ["$_id", 2] }, then: "2-4" },
            { case: { $eq: ["$_id", 4] }, then: "4-6" },
            { case: { $eq: ["$_id", 6] }, then: "6-8" },
            { case: { $eq: ["$_id", 8] }, then: "8-10" }
          ],
          default: "Other"
        }
      },
      count: 1,
      avg_comments: { $round: ["$avg_comments", 2] }
    }
  },
  // Stage 4: Sort by rating range
  {
    $sort: { rating_range: 1 }
  }
])

db.movies.aggregate([
  {
    $match: {
      num_mflix_comments: { $gt: 50 },
      runtime: { $exists: true, $ne: null }
    }
  },
  {
    $group: {
      _id: null,
      avg_runtime: { $avg: "$runtime" },
      min_runtime: { $min: "$runtime" },
      max_runtime: { $max: "$runtime" }
    }
  },
  {
    $project: {
      _id: 0,
      avg_runtime: 1,
      min_runtime: 1,
      max_runtime: 1
    }
  }
])

db.comments.aggregate([
  {
    $match: {
      date: {
        $gte: new Date("2017-09-05T00:00:00.000Z"),
        $lte: new Date("2017-09-11T23:59:59.999Z")
      }
    }
  },
  {
    $group: {
      _id: {
        $dateToString: {
          format: "%Y-%m-%d",
          date: "$date"
        }
      },
      uniqueEmails: { $addToSet: "$email" }
    }
  },
  {
    $project: {
      date: {
        $dateFromString: {
          dateString: "$_id"
        }
      },
      activeUsers: { $size: "$uniqueEmails" }
    }
  },
  {
    $sort: { date: 1 }
  }
])

db.sessions.aggregate([
  // Group sessions by user_id to count sessions per user
  {
    $group: {
      _id: "$user_id",
      sessionCount: { $sum: 1 }
    }
  },
  // Sort by session count in descending order
  {
    $sort: { sessionCount: -1 }
  },
  // Limit to top 20 most active users
  {
    $limit: 20
  },
  // Join with users collection to get user details
  {
    $lookup: {
      from: "users",
      localField: "_id",
      foreignField: "email",
      as: "userDetails"
    }
  },
  // Unwind the user details array
  {
    $unwind: {
      path: "$userDetails",
      preserveNullAndEmptyArrays: true
    }
  },
  // Project final output format
  {
    $project: {
      user_id: "$_id",
      email: { $ifNull: ["$userDetails.email", "$_id"] },
      name: { $ifNull: ["$userDetails.name", "Unknown"] },
      sessionCount: 1,
      _id: 0
    }
  }
])

