db.movies.aggregate([
  {
    $match: {
      directors: "Tim Burton",
      cast: "Johnny Depp",
      year: { $gte: 1990, $lte: 2009 },
      "imdb.rating": { $exists: true, $ne: null }
    }
  },
  {
    $project: {
      decade: {
        $cond: {
          if: { $and: [{ $gte: ["$year", 1990] }, { $lte: ["$year", 1999] }] },
          then: "1990s",
          else: "2000s"
        }
      },
      rating: "$imdb.rating"
    }
  },
  {
    $group: {
      _id: "$decade",
      averageRating: { $avg: "$rating" },
      movieCount: { $sum: 1 }
    }
  },
  {
    $project: {
      decade: "$_id",
      averageRating: { $round: ["$averageRating", 2] },
      movieCount: 1
    }
  },
  {
    $sort: { decade: 1 }
  }
])

