db.movies.aggregate([
  {
    $match: {
      metacritic: { $exists: true, $ne: null }
    }
  },
  {
    $bucket: {
      groupBy: "$metacritic",
      boundaries: [0, 26, 51, 76, 101],
      default: "Other",
      output: {
        movieCount: { $sum: 1 },
        averageComments: { $avg: "$num_mflix_comments" }
      }
    }
  },
  {
    $project: {
      _id: 0,
      scoreRange: {
        $switch: {
          branches: [
            { case: { $eq: ["$_id", 0] }, then: "0-25" },
            { case: { $eq: ["$_id", 26] }, then: "26-50" },
            { case: { $eq: ["$_id", 51] }, then: "51-75" },
            { case: { $eq: ["$_id", 76] }, then: "76-100" }
          ],
          default: "Unknown"
        }
      },
      movieCount: 1,
      averageComments: { $round: ["$averageComments", 2] }
    }
  },
  {
    $sort: { scoreRange: 1 }
  }
])

