db.users.aggregate([
  // Match the specific user by email
  {
    $match: {
      email: "joe_dempsie@gameofthron.es"
    }
  },
  // Lookup comments by this email
  {
    $lookup: {
      from: "comments",
      localField: "email",
      foreignField: "email",
      as: "user_comments"
    }
  },
  // Lookup sessions by user_id (which appears to be email)
  {
    $lookup: {
      from: "sessions",
      localField: "email",
      foreignField: "user_id",
      as: "user_sessions"
    }
  },
  // Project the final result
  {
    $project: {
      user_exists: { $literal: true },
      comment_count: { $size: "$user_comments" },
      has_active_session: { $gt: [{ $size: "$user_sessions" }, 0] },
      user_details: {
        _id: "$_id",
        name: "$name",
        email: "$email"
      }
    }
  },
  // Handle case where user doesn't exist
  {
    $facet: {
      results: [{ $limit: 1 }],
      default: [
        {
          $replaceRoot: {
            newRoot: {
              user_exists: false,
              comment_count: 0,
              has_active_session: false
            }
          }
        }
      ]
    }
  },
  // Return either the result or default if no user found
  {
    $replaceRoot: {
      newRoot: {
        $ifNull: [
          { $arrayElemAt: ["$results", 0] },
          { $arrayElemAt: ["$default", 0] }
        ]
      }
    }
  }
])

