db.comments.aggregate([
  {
    $facet: {
      orphanedNoMovie: [
        {
          $lookup: {
            from: "movies",
            localField: "movie_id",
            foreignField: "_id",
            as: "movie"
          }
        },
        {
          $match: {
            movie: { $size: 0 }
          }
        },
        {
          $count: "count"
        }
      ],
      orphanedNoUser: [
        {
          $lookup: {
            from: "users",
            localField: "email",
            foreignField: "email",
            as: "user"
          }
        },
        {
          $match: {
            user: { $size: 0 }
          }
        },
        {
          $count: "count"
        }
      ]
    }
  },
  {
    $project: {
      orphaned_comments_no_movie: { $ifNull: [{ $arrayElemAt: ["$orphanedNoMovie.count", 0] }, 0] },
      orphaned_comments_no_user: { $ifNull: [{ $arrayElemAt: ["$orphanedNoUser.count", 0] }, 0] }
    }
  },
  {
    $lookup: {
      from: "sessions",
      pipeline: [
        {
          $lookup: {
            from: "users",
            localField: "user_id",
            foreignField: "email",
            as: "user"
          }
        },
        {
          $match: {
            user: { $size: 0 }
          }
        },
        {
          $count: "count"
        }
      ],
      as: "orphanedSessions"
    }
  },
  {
    $project: {
      orphaned_comments_no_movie: 1,
      orphaned_comments_no_user: 1,
      orphaned_sessions: { $ifNull: [{ $arrayElemAt: ["$orphanedSessions.count", 0] }, 0] }
    }
  },
  {
    $addFields: {
      total_orphaned_records: {
        $add: ["$orphaned_comments_no_movie", "$orphaned_comments_no_user", "$orphaned_sessions"]
      }
    }
  }
])

db.movies.aggregate([
  // Stage 1: Match movies released before 1980 with more than 75 comments
  {
    $match: {
      year: { $lt: 1980 },
      num_mflix_comments: { $gt: 75 }
    }
  },
  // Stage 2: Unwind directors array to handle movies with multiple directors
  {
    $unwind: "$directors"
  },
  // Stage 3: Group by director to find those with at least 3 highly-discussed movies
  {
    $group: {
      _id: "$directors",
      movieCount: { $sum: 1 },
      movies: {
        $push: {
          title: "$title",
          year: "$year",
          num_mflix_comments: "$num_mflix_comments"
        }
      }
    }
  },
  // Stage 4: Filter for directors with at least 3 such movies
  {
    $match: {
      movieCount: { $gte: 3 }
    }
  },
  // Stage 5: Sort movies by comment count for each director
  {
    $project: {
      director: "$_id",
      films: {
        $sortArray: {
          input: "$movies",
          sortBy: { num_mflix_comments: -1 }
        }
      }
    }
  },
  // Stage 6: Sort directors by name for consistent output
  {
    $sort: { director: 1 }
  }
])

db.movies.aggregate([
  {
    $match: {
      year: { $gte: 1950, $lte: 1979 },
      num_mflix_comments: { $exists: true, $ne: null }
    }
  },
  {
    $addFields: {
      decade: {
        $switch: {
          branches: [
            { case: { $and: [{ $gte: ["$year", 1950] }, { $lt: ["$year", 1960] }] }, then: "1950s" },
            { case: { $and: [{ $gte: ["$year", 1960] }, { $lt: ["$year", 1970] }] }, then: "1960s" },
            { case: { $and: [{ $gte: ["$year", 1970] }, { $lt: ["$year", 1980] }] }, then: "1970s" }
          ]
        }
      }
    }
  },
  {
    $sort: { num_mflix_comments: -1 }
  },
  {
    $group: {
      _id: "$decade",
      movies: {
        $push: {
          title: "$title",
          year: "$year",
          num_mflix_comments: "$num_mflix_comments",
          imdb: { votes: "$imdb.votes" }
        }
      }
    }
  },
  {
    $project: {
      decade: "$_id",
      movies: { $slice: ["$movies", 5] }
    }
  },
  {
    $sort: { decade: 1 }
  }
])

db.movies.aggregate([
  {
    $match: {
      year: { $lt: 1980 },
      num_mflix_comments: { $gt: 40 },
      "imdb.rating": { $gt: 7.5 },
      "imdb.votes": { $gt: 5000 }
    }
  },
  {
    $addFields: {
      comments_per_year: {
        $divide: [
          "$num_mflix_comments",
          { $subtract: [2017, "$year"] }
        ]
      }
    }
  },
  {
    $sort: { comments_per_year: -1 }
  },
  {
    $limit: 20
  },
  {
    $project: {
      _id: 0,
      title: 1,
      year: 1,
      num_mflix_comments: 1,
      imdb: {
        rating: "$imdb.rating",
        votes: "$imdb.votes"
      },
      comments_per_year: 1
    }
  }
])

