  },
  {
    $bucket: {
      groupBy: "$imdb.rating",
      boundaries: [0, 5, 7, 8.5, 10],
      default: "Unknown",
      output: {
        movieCount: { $sum: 1 },
        avgViewerMeter: { $avg: "$tomatoes.viewer.meter" },
        avgCriticMeter: { $avg: "$tomatoes.critic.meter" }
      }
    }
  },
  {
    $project: {
      _id: 0,
      ratingRange: {
        $switch: {
          branches: [
            { case: { $eq: ["$_id", 0] }, then: "0-5" },
            { case: { $eq: ["$_id", 5] }, then: "5-7" },
            { case: { $eq: ["$_id", 7] }, then: "7-8.5" },
            { case: { $eq: ["$_id", 8.5] }, then: "8.5-10" }
          ],
          default: "Unknown"
        }
      },
      movieCount: 1,
      avgViewerMeter: { $round: ["$avgViewerMeter", 1] },
      avgCriticMeter: { $round: ["$avgCriticMeter", 1] }
    }
  },
