db.comments.aggregate([
  {
    $match: {
      date: {
        $gte: new Date("2017-07-01"),
        $lt: new Date("2017-09-01")
      }
    }
  },
  {
    $project: {
      movie_id: 1,
      month: { $month: "$date" }
    }
  },
  {
    $group: {
      _id: {
        movie_id: "$movie_id",
        month: "$month"
      },
      count: { $sum: 1 }
    }
  },
  {
    $group: {
      _id: "$_id.movie_id",
      monthCounts: {
        $push: {
          month: "$_id.month",
          count: "$count"
        }
      }
    }
  },
  {
    $project: {
      julyComments: {
        $ifNull: [
          {
            $arrayElemAt: [
              {
                $map: {
                  input: {
                    $filter: {
                      input: "$monthCounts",
                      cond: { $eq: ["$$this.month", 7] }
                    }
                  },
                  in: "$$this.count"
                }
              },
              0
            ]
          },
          0
        ]
      },
      augustComments: {
        $ifNull: [
          {
            $arrayElemAt: [
              {
                $map: {
                  input: {
                    $filter: {
                      input: "$monthCounts",
                      cond: { $eq: ["$$this.month", 8] }
                    }
                  },
                  in: "$$this.count"
                }
              },
              0
            ]
          },
          0
        ]
      }
    }
  },
  {
    $addFields: {
      commentIncrease: { $subtract: ["$augustComments", "$julyComments"] }
    }
  },
  {
    $match: {
      commentIncrease: { $gt: 0 }
    }
  },
  {
    $sort: { commentIncrease: -1 }
  },
  {
    $limit: 7
  },
  {
    $lookup: {
      from: "movies",
      localField: "_id",
      foreignField: "_id",
      as: "movie"
    }
  },
  {
    $unwind: "$movie"
  },
  {
    $project: {
      title: "$movie.title",
      julyComments: 1,
      augustComments: 1,
      growthRate: {
        $cond: [
          { $eq: ["$julyComments", 0] },
          { $multiply: ["$augustComments", 100] },
          {
            $multiply: [
              { $divide: [{ $subtract: ["$augustComments", "$julyComments"] }, "$julyComments"] },
              100
            ]
          }
        ]
      }
    }
  }
])

