db.comments.aggregate([
  // First, get all comments and group by user
  {
    $group: {
      _id: "$email",
      comments: {
        $push: {
          date: "$date"
        }
      },
      lastCommentDate: { $max: "$date" }
    }
  },
  // Filter users who had comments in 2016
  {
    $match: {
      comments: {
        $elemMatch: {
          date: {
            $gte: new Date("2016-01-01"),
            $lte: new Date("2016-12-31")
          }
        }
      }
    }
  },
  // Filter out users who commented in the last 6 months before Sept 12, 2017
  {
    $match: {
      $nor: [
        {
          comments: {
            $elemMatch: {
              date: {
                $gte: new Date("2017-03-12"),
                $lte: new Date("2017-09-12")
              }
            }
          }
        }
      ]
    }
  },
  // Join with users collection to get user details
  {
    $lookup: {
      from: "users",
      localField: "_id",
      foreignField: "email",
      as: "userInfo"
    }
  },
  // Unwind the user info
  {
    $unwind: "$userInfo"
  },
  // Project the required fields
  {
    $project: {
      name: "$userInfo.name",
      email: "$_id",
      lastCommentDate: 1
    }
  },
  // Sort by last comment date (most recent first)
  {
    $sort: { lastCommentDate: -1 }
  },
  // Limit to 15 results
  {
    $limit: 15
  }
])

