db.movies.aggregate([
  // First, unwind countries array to work with individual countries
  {
    $unwind: "$countries"
  },

  // Filter for African countries
  {
    $match: {
      countries: {
        $in: [
          "Egypt", "South Africa", "Nigeria", "Kenya", "Morocco", "Tunisia", 
          "Algeria", "Ethiopia", "Ghana", "Zimbabwe", "Tanzania", "Uganda",
          "Senegal", "Cameroon", "Rwanda", "Botswana", "Namibia", "Zambia",
          "Mozambique", "Angola", "Libya", "Sudan", "Mali", "Burkina Faso",
          "Madagascar", "Ivory Coast", "Niger", "Chad", "Guinea", "Benin",
          "Togo", "Sierra Leone", "Liberia", "Mauritania", "Eritrea", "Gambia",
          "Gabon", "Mauritius", "Swaziland", "Lesotho", "Equatorial Guinea",
          "Djibouti", "Comoros", "Cape Verde", "Sao Tome and Principe", "Seychelles"
        ]
      }
    }
  },

  // Group by country to collect movies and their ratings
  {
    $group: {
      _id: "$countries",
      totalMovies: { $sum: 1 },
      movies: {
        $push: {
          title: "$title",
          rating: "$imdb.rating"
        }
      }
    }
  },

  // Add a field for high-rated movies (rating > 7.0)
  {
    $addFields: {
      highRatedMovies: {
        $filter: {
          input: "$movies",
          as: "movie",
          cond: { $gt: ["$$movie.rating", 7.0] }
        }
      }
    }
  },

  // Filter for countries with < 3 movies total and at least 1 high-rated movie
  {
    $match: {
      totalMovies: { $lt: 3 },
      $expr: { $gt: [{ $size: "$highRatedMovies" }, 0] }
    }
  },

  // Project the final output format
  {
    $project: {
      country: "$_id",
      totalMovies: 1,
      highRatedMovies: {
        $map: {
          input: "$highRatedMovies",
          as: "movie",
          in: {
            title: "$$movie.title",
            rating: "$$movie.rating"
          }
        }
      }
    }
  },

  // Sort by country name for consistent output
  {
    $sort: { country: 1 }
  }
])

