db.movies.aggregate([
  // Stage 1: Filter movies from Nigeria or Kenya released after 2005
  {
    $match: {
      countries: { $in: ["Nigeria", "Kenya"] },
      released: { $gt: new Date("2005-12-31") }
    }
  },
  // Stage 2: Lookup comments for these movies
  {
    $lookup: {
      from: "comments",
      localField: "_id",
      foreignField: "movie_id",
      as: "comments"
    }
  },
  // Stage 3: Unwind comments array to get individual comment documents
  {
    $unwind: "$comments"
  },
  // Stage 4: Unwind countries array to get individual country values
  {
    $unwind: "$countries"
  },
  // Stage 5: Filter to only keep Nigeria or Kenya from countries
  {
    $match: {
      countries: { $in: ["Nigeria", "Kenya"] }
    }
  },
  // Stage 6: Sort by comment date descending (most recent first)
  {
    $sort: { "comments.date": -1 }
  },
  // Stage 7: Limit to 15 most recent comments
  {
    $limit: 15
  },
  // Stage 8: Project only required fields
  {
    $project: {
      commentText: "$comments.text",
      movieTitle: "$title",
      movieCountry: "$countries",
      commentDate: "$comments.date",
      commenterName: "$comments.name"
    }
  }
])

db.movies.aggregate([
  // Filter for documentaries
  {
    $match: {
      genres: "Documentary"
    }
  },
  // Sort by number of comments (descending)
  {
    $sort: {
      num_mflix_comments: -1
    }
  },
  // Get top 8
  {
    $limit: 8
  },
  // Lookup comments for each movie
  {
    $lookup: {
      from: "comments",
      localField: "_id",
      foreignField: "movie_id",
      as: "all_comments"
    }
  },
  // Process comments to get only 2017 comments
  {
    $addFields: {
      comments_2017: {
        $filter: {
          input: "$all_comments",
          cond: {
            $and: [
              { $gte: ["$$this.date", new Date("2017-01-01")] },
              { $lt: ["$$this.date", new Date("2018-01-01")] }
            ]
          }
        }
      }
    }
  },
  // Sort comments by date and get recent ones
  {
    $addFields: {
      recent_comments: {
        $map: {
          input: {
            $slice: [
              {
                $sortArray: {
                  input: "$comments_2017",
                  sortBy: { date: -1 }
                }
              },
              3 // Get up to 3 most recent comments from 2017
            ]
          },
          as: "comment",
          in: "$$comment.text"
        }
      }
    }
  },
  // Project final fields
  {
    $project: {
      title: 1,
      countries: 1,
      num_mflix_comments: 1,
      imdb_rating: "$imdb.rating",
      recent_comments: 1
    }
  }
])

