db.movies.aggregate([
  // Match action movies from target countries released between 2010-2017
  {
    $match: {
      genres: "Action",
      countries: { $in: ["South Korea", "Japan", "Hong Kong"] },
      released: {
        $gte: new Date("2010-01-01"),
        $lt: new Date("2018-01-01")
      }
    }
  },
  // Join with comments collection
  {
    $lookup: {
      from: "comments",
      localField: "_id",
      foreignField: "movie_id",
      as: "comments"
    }
  },
  // Unwind countries to process each country separately
  {
    $unwind: "$countries"
  },
  // Filter to only keep our target countries
  {
    $match: {
      countries: { $in: ["South Korea", "Japan", "Hong Kong"] }
    }
  },
  // Group by country to calculate statistics
  {
    $group: {
      _id: "$countries",
      totalMovies: { $sum: 1 },
      totalComments: { $sum: { $size: "$comments" } },
      allComments: { $push: "$comments" }
    }
  },
  // Process comment texts
  {
    $project: {
      country: "$_id",
      totalMovies: 1,
      totalComments: 1,
      avgCommentsPerMovie: {
        $cond: {
          if: { $gt: ["$totalMovies", 0] },
          then: { $divide: ["$totalComments", "$totalMovies"] },
          else: 0
        }
      },
      // Flatten all comments and extract text
      sampleComments: {
        $slice: [
          {
            $map: {
              input: {
                $reduce: {
                  input: "$allComments",
                  initialValue: [],
                  in: { $concatArrays: ["$$value", "$$this"] }
                }
              },
              as: "comment",
              in: "$$comment.text"
            }
          },
          5 // Get first 5 comment texts as samples
        ]
      }
    }
  },
  // Remove the _id field and sort by country name
  {
    $project: {
      _id: 0,
      country: 1,
      totalMovies: 1,
      totalComments: 1,
      avgCommentsPerMovie: { $round: ["$avgCommentsPerMovie", 2] },
      sampleComments: 1
    }
  },
  {
    $sort: { country: 1 }
  }
])

