  {
    $match: {
      "tomatoes.viewer.meter": { $exists: true, $ne: null },
      "tomatoes.critic.meter": { $exists: true, $ne: null },
      "tomatoes.viewer.numReviews": { $gt: 10000 }
    }
  },
  {
    $addFields: {
      meterDifference: {
        $subtract: ["$tomatoes.viewer.meter", "$tomatoes.critic.meter"]
      }
    }
  },
  {
    $match: {
      meterDifference: { $gte: 30 }
    }
  },
  {
    $project: {
      title: 1,
      tomatoes: {
        viewer: {
          meter: 1,
          numReviews: 1
        },
        critic: {
          meter: 1
        }
      }
    }
  },
  {
    $limit: 12
  }
])

db.movies.aggregate([
  {
    $match: {
      year: { $gte: 2015, $lte: 2017 },
      "imdb.rating": { $exists: true, $ne: null, $gt: 0 },
      "tomatoes.critic.rating": { $exists: true, $ne: null, $gt: 0 },
      metacritic: { $exists: true, $ne: null, $gt: 0 }
    }
  },
  {
    $project: {
      title: 1,
      year: 1,
      imdb: { rating: "$imdb.rating" },
      tomatoesCritic: { rating: "$tomatoes.critic.rating" },
      metacritic: 1,
      // Normalize all ratings to 0-10 scale
      normalizedRatings: [
        "$imdb.rating",
        "$tomatoes.critic.rating",
        { $divide: ["$metacritic", 10] }
      ]
    }
  },
  {
    $addFields: {
      // Calculate mean of the three ratings
      meanRating: { $avg: "$normalizedRatings" },
      // Calculate standard deviation
      ratingStandardDeviation: {
        $sqrt: {
          $avg: {
            $map: {
              input: "$normalizedRatings",
              as: "rating",
              in: {
                $pow: [
                  { $subtract: ["$$rating", { $avg: "$normalizedRatings" }] },
                  2
                ]
              }
            }
          }
        }
      }
    }
  },
  {
    $sort: { meanRating: -1 }
  },
  {
    $limit: 10
  },
  {
    $project: {
      _id: 0,
      title: 1,
      year: 1,
      imdb: 1,
      tomatoesCritic: 1,
      metacritic: 1,
      ratingStandardDeviation: 1
    }
  }
])

db.movies.aggregate([
  {
    $match: {
      genres: { $in: ["Drama", "Documentary"] },
      released: { $gt: new Date("2005-12-31") },
      "imdb.rating": { $exists: true, $ne: null },
      "tomatoes.critic.rating": { $exists: true, $ne: null },
      metacritic: { $exists: true, $ne: null }
    }
  },
  {
    $unwind: "$genres"
  },
  {
    $match: {
      genres: { $in: ["Drama", "Documentary"] }
    }
  },
  {
    $group: {
      _id: "$genres",
      movieCount: { $sum: 1 },
      avgIMDbRating: { $avg: "$imdb.rating" },
      avgTomatoesCriticRating: { $avg: "$tomatoes.critic.rating" },
      avgMetacriticScore: { $avg: "$metacritic" }
    }
  },
  {
    $match: {
      movieCount: { $gte: 20 }
    }
  },
  {
    $project: {
      genre: "$_id",
      movieCount: 1,
      avgIMDbRating: { $round: ["$avgIMDbRating", 2] },
      avgTomatoesCriticRating: { $round: ["$avgTomatoesCriticRating", 2] },
      avgMetacriticScore: { $round: ["$avgMetacriticScore", 2] },
      _id: 0
    }
  },
  {
    $sort: { genre: 1 }
  }
])
