dbQuery:
db.temp_neighborhood_counts.find(
  { chineseCount: { $gt: 0 } },
  { name: 1, chineseCount: 1, _id: 0 }
)
.sort({ chineseCount: -1 })
.limit(15)

db.restaurants.aggregate([
  {
    $match: {
      cuisine: "Chinese",
      borough: { $in: ["Manhattan", "Queens"] }
    }
  },
  {
    $group: {
      _id: "$borough",
      chineseRestaurantCount: { $sum: 1 }
    }
  },
  {
    $project: {
      _id: 0,
      borough: "$_id",
      chineseRestaurantCount: 1
    }
  },
  {
    $sort: { borough: 1 }
  }
])

