db.temp_neighborhood_counts.aggregate([
  {
    $match: {
      chineseCount: { $gte: 5 }
    }
  },
  {
    $addFields: {
      chinesePercentage: {
        $multiply: [
          { $divide: ["$chineseCount", "$count"] },
          100
        ]
      }
    }
  },
  {
    $match: {
      chinesePercentage: { $gt: 20 }
    }
  },
  {
    $project: {
      _id: 0,
      name: 1,
      chineseCount: 1,
      totalCount: "$count",
      chinesePercentage: 1
    }
  },
  {
    $sort: { chinesePercentage: -1 }
  }
])

db.restaurants.aggregate([
  {
    $group: {
      _id: "$borough",
      totalRestaurants: { $sum: 1 },
      chineseRestaurants: {
        $sum: {
          $cond: [
            { $eq: ["$cuisine", "Chinese"] },
            1,
            0
          ]
        }
      }
    }
  },
  {
    $project: {
      borough: "$_id",
      totalRestaurants: 1,
      chineseRestaurants: 1,
      chinesePercentage: {
        $multiply: [
          { $divide: ["$chineseRestaurants", "$totalRestaurants"] },
          100
        ]
      },
      _id: 0
    }
  },
  {
    $sort: { borough: 1 }
  }
])

db.temp_neighborhood_counts.aggregate([
  {
    $lookup: {
      from: "restaurants",
      let: { neighborhoodName: "$name" },
      pipeline: [
        {
          $match: {
            $expr: { $eq: ["$borough", "Queens"] }
          }
        },
        {
          $limit: 1
        }
      ],
      as: "queensRestaurants"
    }
  },
  {
    $match: {
      queensRestaurants: { $ne: [] },
      $or: [
        { name: { $regex: "China", $options: "i" } },
        { chineseCount: { $gt: 10 } }
      ]
    }
  },
  {
    $project: {
      _id: 0,
      neighborhoodName: "$name",
      chineseRestaurantCount: "$chineseCount",
      totalRestaurantCount: "$count"
    }
  },
  {
    $sort: { chineseRestaurantCount: -1 }
  }
])

db.restaurants.aggregate([
  // Stage 1: Filter for Chinese restaurants only
  {
    $match: {
      cuisine: "Chinese"
    }
  },
  // Stage 2: Add fields to check for improvement
  {
    $addFields: {
      // Filter grades to only include those between 2018 and 2024
      relevantGrades: {
        $filter: {
          input: "$grades",
          cond: {
            $and: [
              { $gte: ["$$this.date", new Date("2018-01-01")] },
              { $lte: ["$$this.date", new Date("2024-12-31")] }
            ]
          }
        }
      }
    }
  },
  // Stage 3: Determine if restaurant improved
  {
    $addFields: {
      improved: {
        $let: {
          vars: {
            // Find the latest B or C grade
            lastBorC: {
              $arrayElemAt: [
                {
                  $filter: {
                    input: "$relevantGrades",
                    cond: { $in: ["$$this.grade", ["B", "C"]] }
                  }
                },
                0
              ]
            }
          },
          in: {
            $and: [
              // Must have at least one B or C grade
              { $ne: ["$$lastBorC", null] },
              // Must have an A grade after the B or C grade
              {
                $gt: [
                  {
                    $size: {
                      $filter: {
                        input: "$relevantGrades",
                        cond: {
                          $and: [
                            { $eq: ["$$this.grade", "A"] },
                            { $lt: ["$$lastBorC.date", "$$this.date"] }
                          ]
                        }
                      }
                    }
                  },
                  0
                ]
              }
            ]
          }
        }
      }
    }
  },
  // Stage 4: Group by borough and calculate statistics
  {
    $group: {
      _id: "$borough",
      totalChineseRestaurants: { $sum: 1 },
      improvedRestaurants: {
        $sum: {
          $cond: [{ $eq: ["$improved", true] }, 1, 0]
        }
      }
    }
  },
  // Stage 5: Calculate improvement percentage
  {
    $project: {
      borough: "$_id",
      totalChineseRestaurants: 1,
      improvedRestaurants: 1,
      improvementPercentage: {
        $round: [
          {
            $multiply: [
              {
                $divide: ["$improvedRestaurants", "$totalChineseRestaurants"]
              },
              100
            ]
          },
          2
        ]
      }
    }
  },
  // Stage 6: Sort by borough name
  {
    $sort: { borough: 1 }
  }
])

db.restaurants.aggregate([
  // Stage 1: Filter for Brooklyn restaurants only
  {
    $match: {
      borough: "Brooklyn"
    }
  },
  // Stage 2: Group by street and zipcode, counting by cuisine type
  {
    $group: {
      _id: {
        street: "$address.street",
        zipcode: "$address.zipcode"
      },
      pizzaCount: {
        $sum: {
          $cond: [
            { $eq: ["$cuisine", "Pizza"] },
            1,
            0
          ]
        }
      },
      latinCount: {
        $sum: {
          $cond: [
            { $eq: ["$cuisine", "Latin (Cuban, Dominican, Puerto Rican, South & Central American)"] },
            1,
            0
          ]
        }
      },
      totalCount: { $sum: 1 }
    }
  },
  // Stage 3: Filter for streets meeting both criteria
  {
    $match: {
      pizzaCount: { $gte: 3 },
      latinCount: { $gte: 2 }
    }
  },
  // Stage 4: Project to match the required output format
  {
    $project: {
      _id: 0,
      street: "$_id.street",
      zipcode: "$_id.zipcode",
      pizzaCount: 1,
      latinCount: 1,
      totalCount: 1
    }
  },
  // Stage 5: Sort by total count descending for better readability
  {
    $sort: { totalCount: -1 }
  }
])

