db.restaurants.aggregate([
  {
    $match: {
      "address.zipcode": { $in: ["10003", "10009"] },
      "cuisine": "Chinese",
      "grades.1": { $exists: true } // Ensure at least 2 grades exist
    }
  },
  {
    $addFields: {
      // Sort grades by date descending to ensure most recent is first
      sortedGrades: {
        $sortArray: {
          input: "$grades",
          sortBy: { date: -1 }
        }
      }
    }
  },
  {
    $match: {
      $expr: {
        $and: [
          { $eq: [{ $arrayElemAt: ["$sortedGrades.grade", 0] }, "A"] }, // Most recent grade is A
          { $in: [{ $arrayElemAt: ["$sortedGrades.grade", 1] }, ["B", "C"]] } // Previous grade is B or C
        ]
      }
    }
  },
  {
    $project: {
      name: 1,
      address: {
        building: "$address.building",
        street: "$address.street",
        zipcode: "$address.zipcode"
      },
      currentInspection: {
        grade: { $arrayElemAt: ["$sortedGrades.grade", 0] },
        score: { $arrayElemAt: ["$sortedGrades.score", 0] },
        date: { $arrayElemAt: ["$sortedGrades.date", 0] }
      },
      previousInspection: {
        grade: { $arrayElemAt: ["$sortedGrades.grade", 1] },
        score: { $arrayElemAt: ["$sortedGrades.score", 1] },
        date: { $arrayElemAt: ["$sortedGrades.date", 1] }
      }
    }
  },
  {
    $sort: { name: 1 }
  }
])

