db.restaurants.aggregate([
  {
    $addFields: {
      hasAGrade: {
        $in: ["A", "$grades.grade"]
      }
    }
  },
  {
    $group: {
      _id: "$address.zipcode",
      totalRestaurants: { $sum: 1 },
      restaurantsWithAGrade: {
        $sum: {
          $cond: ["$hasAGrade", 1, 0]
        }
      }
    }
  },
  {
    $addFields: {
      percentageAGrade: {
        $multiply: [
          {
            $divide: ["$restaurantsWithAGrade", "$totalRestaurants"]
          },
          100
        ]
      }
    }
  },
  {
    $match: {
      percentageAGrade: { $lt: 50 }
    }
  },
  {
    $project: {
      _id: 0,
      zipcode: "$_id",
      percentageAGrade: { $round: ["$percentageAGrade", 2] },
      totalRestaurants: 1
    }
  },
  {
    $sort: { percentageAGrade: 1 }
  }
])

db.restaurants.aggregate([
  // Match restaurants in Manhattan first (using borough index)
  {
    $match: {
      borough: "Manhattan"
    }
  },
  // Filter for restaurants with most recent grade B or C
  {
    $match: {
      "grades.0.grade": { $in: ["B", "C"] }
    }
  },
  // Filter by approximate geographic boundaries for Tribeca and SoHo
  // Tribeca roughly: south of Canal St, north of Chambers St, west of Broadway
  // SoHo roughly: south of Houston St, north of Canal St, between 6th Ave and Lafayette
  {
    $match: {
      $or: [
        // Tribeca approximate boundaries
        {
          "address.coord.1": { $gte: 40.7163, $lte: 40.7270 },
          "address.coord.0": { $gte: -74.0130, $lte: -74.0030 }
        },
        // SoHo approximate boundaries
        {
          "address.coord.1": { $gte: 40.7210, $lte: 40.7280 },
          "address.coord.0": { $gte: -74.0050, $lte: -73.9940 }
        }
      ]
    }
  },
  // Sort by most recent inspection date
  {
    $sort: {
      "grades.0.date": -1
    }
  },
  // Limit to 15 results
  {
    $limit: 15
  },
  // Project only required fields
  {
    $project: {
      name: 1,
      address: {
        building: "$address.building",
        street: "$address.street"
      },
      lastGrade: { $arrayElemAt: ["$grades.grade", 0] },
      lastInspectionDate: { $arrayElemAt: ["$grades.date", 0] }
    }
  }
])

