db.restaurants.aggregate([
  // Match restaurants in the specified zip codes
  {
    $match: {
      "address.zipcode": {
        $in: ["10013", "10014", "10011", "10451", "10452", "10453"]
      }
    }
  },
  // Add a field to categorize zip codes and check if restaurant has A grade
  {
    $addFields: {
      zipCategory: {
        $cond: {
          if: { $in: ["$address.zipcode", ["10013", "10014", "10011"]] },
          then: "highRent",
          else: "lowRent"
        }
      },
      hasAGrade: {
        $gt: [
          {
            $size: {
              $filter: {
                input: "$grades",
                cond: { $eq: ["$$this.grade", "A"] }
              }
            }
          },
          0
        ]
      }
    }
  },
  // Group by zip code to get counts
  {
    $group: {
      _id: {
        zipcode: "$address.zipcode",
        category: "$zipCategory"
      },
      totalRestaurants: { $sum: 1 },
      aGradeRestaurants: {
        $sum: { $cond: ["$hasAGrade", 1, 0] }
      }
    }
  },
  // Calculate percentage for each zip code
  {
    $addFields: {
      aGradePercentage: {
        $multiply: [
          { $divide: ["$aGradeRestaurants", "$totalRestaurants"] },
          100
        ]
      }
    }
  },
  // Group by category to get average percentage
  {
    $group: {
      _id: "$_id.category",
      zipcodes: { $push: "$_id.zipcode" },
      averageAGradePercentage: { $avg: "$aGradePercentage" },
      totalRestaurants: { $sum: "$totalRestaurants" }
    }
  },
  // Format the final output
  {
    $group: {
      _id: null,
      results: {
        $push: {
          k: "$_id",
          v: {
            zipcodes: "$zipcodes",
            averageAGradePercentage: "$averageAGradePercentage",
            totalRestaurants: "$totalRestaurants"
          }
        }
      }
    }
  },
  {
    $replaceRoot: {
      newRoot: { $arrayToObject: "$results" }
    }
  },
  // Rename fields to match expected schema
  {
    $project: {
      highRentZips: "$highRent",
      lowRentZips: "$lowRent"
    }
  }
])

