db.restaurants.aggregate([
  // Filter out restaurants with less than 2 grades
  {
    $match: {
      "grades.1": { $exists: true }
    }
  },
  // Add fields for analysis
  {
    $addFields: {
      // Extract just the grade letters in chronological order
      gradeLetters: {
        $map: {
          input: { $reverseArray: "$grades" },
          as: "grade",
          in: "$$grade.grade"
        }
      }
    }
  },
  // Calculate grade changes
  {
    $addFields: {
      gradeChanges: {
        $reduce: {
          input: { $range: [1, { $size: "$gradeLetters" }] },
          initialValue: 0,
          in: {
            $add: [
              "$$value",
              {
                $cond: [
                  {
                    $ne: [
                      { $arrayElemAt: ["$gradeLetters", { $subtract: ["$$this", 1] }] },
                      { $arrayElemAt: ["$gradeLetters", "$$this"] }
                    ]
                  },
                  1,
                  0
                ]
              }
            ]
          }
        }
      }
    }
  },
  // Filter to only include restaurants with at least one grade change
  {
    $match: {
      gradeChanges: { $gt: 0 }
    }
  },
  // Sort by most grade changes
  {
    $sort: { gradeChanges: -1 }
  },
  // Limit to top 12
  {
    $limit: 12
  },
  // Project final output
  {
    $project: {
      name: 1,
      borough: 1,
      gradeChanges: 1,
      gradeHistory: "$gradeLetters"
    }
  }
])

