db.restaurants.aggregate([
  // First, find all restaurants within 300m of any of the three stations
  {
    $geoNear: {
      near: { type: "Point", coordinates: [-73.9897, 40.7359] }, // Union Square as reference
      distanceField: "distFromUnion",
      maxDistance: 300,
      spherical: true
    }
  },
  // Add distance calculations for other stations
  {
    $addFields: {
      distFrom14th: {
        $sqrt: {
          $add: [
            { $pow: [{ $multiply: [{ $subtract: [{ $arrayElemAt: ["$address.coord", 0] }, -73.9969] }, 111320] }, 2] },
            { $pow: [{ $multiply: [{ $subtract: [{ $arrayElemAt: ["$address.coord", 1] }, 40.7381] }, 110540] }, 2] }
          ]
        }
      },
      distFromAstor: {
        $sqrt: {
          $add: [
            { $pow: [{ $multiply: [{ $subtract: [{ $arrayElemAt: ["$address.coord", 0] }, -73.9917] }, 111320] }, 2] },
            { $pow: [{ $multiply: [{ $subtract: [{ $arrayElemAt: ["$address.coord", 1] }, 40.7304] }, 110540] }, 2] }
          ]
        }
      }
    }
  },
  // Filter to include restaurants within 300m of at least one station
  {
    $match: {
      $or: [
        { distFromUnion: { $lte: 300 } },
        { distFrom14th: { $lte: 300 } },
        { distFromAstor: { $lte: 300 } }
      ]
    }
  },
  // Mark which stations each restaurant is near
  {
    $addFields: {
      nearUnion: { $lte: ["$distFromUnion", 300] },
      near14th: { $lte: ["$distFrom14th", 300] },
      nearAstor: { $lte: ["$distFromAstor", 300] },
      stationCount: {
        $add: [
          { $cond: [{ $lte: ["$distFromUnion", 300] }, 1, 0] },
          { $cond: [{ $lte: ["$distFrom14th", 300] }, 1, 0] },
          { $cond: [{ $lte: ["$distFromAstor", 300] }, 1, 0] }
        ]
      }
    }
  },
  // Group to calculate totals
  {
    $group: {
      _id: null,
      totalRestaurants: { $sum: 1 },
      nearUnionCount: { $sum: { $cond: ["$nearUnion", 1, 0] } },
      near14thCount: { $sum: { $cond: ["$near14th", 1, 0] } },
      nearAstorCount: { $sum: { $cond: ["$nearAstor", 1, 0] } },
      sharedByMultiple: { $sum: { $cond: [{ $gt: ["$stationCount", 1] }, 1, 0] } }
    }
  },
  // Format the output
  {
    $project: {
      _id: 0,
      results: [
        {
          station: "Union Square",
          coordinates: [-73.9897, 40.7359],
          restaurantCount: "$nearUnionCount",
          sharedRestaurants: "$sharedByMultiple"
        },
        {
          station: "14th St",
          coordinates: [-73.9969, 40.7381],
          restaurantCount: "$near14thCount",
          sharedRestaurants: "$sharedByMultiple"
        },
        {
          station: "Astor Place",
          coordinates: [-73.9917, 40.7304],
          restaurantCount: "$nearAstorCount",
          sharedRestaurants: "$sharedByMultiple"
        }
      ]
    }
  },
  { $unwind: "$results" },
  { $replaceRoot: { newRoot: "$results" } }
])

db.restaurants.aggregate([
  // Unwind the grades array to process each grade individually
  { $unwind: "$grades" },

  // Filter only for grades A, B, and C
  { $match: { "grades.grade": { $in: ["A", "B", "C"] } } },

  // Group by borough and grade to get counts
  { $group: {
    _id: {
      borough: "$borough",
      grade: "$grades.grade"
    },
    count: { $sum: 1 }
  }},

  // Reshape data to have one document per borough with grade counts
  { $group: {
    _id: "$_id.borough",
    grades: {
      $push: {
        grade: "$_id.grade",
        count: "$count"
      }
    }
  }},

  // Project to format the output with separate fields for each grade
  { $project: {
    borough: "$_id",
    gradeA: {
      $ifNull: [
        { $arrayElemAt: [
          { $map: {
            input: { $filter: {
              input: "$grades",
              as: "g",
              cond: { $eq: ["$$g.grade", "A"] }
            }},
            as: "grade",
            in: "$$grade.count"
          }},
          0
        ]},
        0
      ]
    },
    gradeB: {
      $ifNull: [
        { $arrayElemAt: [
          { $map: {
            input: { $filter: {
              input: "$grades",
              as: "g",
              cond: { $eq: ["$$g.grade", "B"] }
            }},
            as: "grade",
            in: "$$grade.count"
          }},
          0
        ]},
        0
      ]
    },
    gradeC: {
      $ifNull: [
        { $arrayElemAt: [
          { $map: {
            input: { $filter: {
              input: "$grades",
              as: "g",
              cond: { $eq: ["$$g.grade", "C"] }
            }},
            as: "grade",
            in: "$$grade.count"
          }},
          0
        ]},
        0
      ]
    }
  }},

  // Sort by borough name alphabetically
  { $sort: { borough: 1 } }
])

