db.neighborhoods.aggregate([
  // Step 1: Calculate centroid for each neighborhood
  {
    $addFields: {
      centerPoint: {
        $cond: {
          if: { $eq: ["$geometry.type", "Polygon"] },
          then: {
            $let: {
              vars: {
                coords: { $arrayElemAt: ["$geometry.coordinates", 0] }
              },
              in: {
                $reduce: {
                  input: "$$coords",
                  initialValue: { sumLng: 0, sumLat: 0, count: 0 },
                  in: {
                    sumLng: { $add: ["$$value.sumLng", { $arrayElemAt: ["$$this", 0] }] },
                    sumLat: { $add: ["$$value.sumLat", { $arrayElemAt: ["$$this", 1] }] },
                    count: { $add: ["$$value.count", 1] }
                  }
                }
              }
            }
          },
          else: {
            // For MultiPolygon, take the first polygon's centroid
            $let: {
              vars: {
                coords: { $arrayElemAt: [{ $arrayElemAt: ["$geometry.coordinates", 0] }, 0] }
              },
              in: {
                $reduce: {
                  input: "$$coords",
                  initialValue: { sumLng: 0, sumLat: 0, count: 0 },
                  in: {
                    sumLng: { $add: ["$$value.sumLng", { $arrayElemAt: ["$$this", 0] }] },
                    sumLat: { $add: ["$$value.sumLat", { $arrayElemAt: ["$$this", 1] }] },
                    count: { $add: ["$$value.count", 1] }
                  }
                }
              }
            }
          }
        }
      }
    }
  },
  // Step 2: Calculate actual center coordinates
  {
    $addFields: {
      centerCoords: [
        { $divide: ["$centerPoint.sumLng", "$centerPoint.count"] },
        { $divide: ["$centerPoint.sumLat", "$centerPoint.count"] }
      ]
    }
  },
  // Step 3: Lookup restaurants near each neighborhood center
  {
    $lookup: {
      from: "restaurants",
      let: { center: "$centerCoords" },
      pipeline: [
        {
          $geoNear: {
            near: { type: "Point", coordinates: "$$center" },
            distanceField: "distance",
            maxDistance: 804.672, // 0.5 miles in meters
            spherical: true
          }
        },
        { $limit: 1 } // We only need to know if any exist
      ],
      as: "nearbyRestaurants"
    }
  },
  // Step 4: Also get the nearest restaurant regardless of distance
  {
    $lookup: {
      from: "restaurants",
      let: { center: "$centerCoords" },
      pipeline: [
        {
          $geoNear: {
            near: { type: "Point", coordinates: "$$center" },
            distanceField: "distance",
            spherical: true
          }
        },
        { $limit: 1 },
        { $project: { distance: 1, borough: 1 } }
      ],
      as: "nearestRestaurant"
    }
  },
  // Step 5: Filter for neighborhoods with no restaurants within 0.5 miles
  {
    $match: {
      nearbyRestaurants: { $size: 0 }
    }
  },
  // Step 6: Extract borough from nearest restaurant and filter outer boroughs
  {
    $addFields: {
      borough: { $arrayElemAt: ["$nearestRestaurant.borough", 0] },
      nearestDistance: { $arrayElemAt: ["$nearestRestaurant.distance", 0] }
    }
  },
  // Step 7: Filter to only outer boroughs
  {
    $match: {
      borough: { $in: ["Brooklyn", "Queens", "The Bronx", "Bronx", "Staten Island"] }
    }
  },
  // Step 8: Project final results
  {
    $project: {
      neighborhood: "$name",
      borough: 1,
      centerPoint: "$centerCoords",
      nearestRestaurantDistance: { $round: ["$nearestDistance", 2] }
    }
  }
])

