      cuisine: "Chinese"
    }
  },
  // Stage 2: Unwind grades array to analyze individual inspections
  {
    $unwind: "$grades"
  },
  // Stage 3: Group by borough and grade to calculate statistics
  {
    $group: {
      _id: {
        borough: "$borough",
        grade: "$grades.grade"
      },
      gradeCount: { $sum: 1 },
      totalScore: { $sum: "$grades.score" },
      restaurantIds: { $addToSet: "$restaurant_id" }
    }
  },
  // Stage 4: Regroup by borough to build grade distribution
  {
    $group: {
      _id: "$_id.borough",
      gradeDistribution: {
        $push: {
          grade: "$_id.grade",
          count: "$gradeCount"
        }
      },
      totalScores: { $sum: "$totalScore" },
      totalInspections: { $sum: "$gradeCount" },
      allRestaurantIds: { $push: "$restaurantIds" }
    }
  },
  // Stage 5: Calculate unique restaurant count and format output
  {
    $project: {
      _id: 0,
      borough: "$_id",
      averageScore: {
        $round: [
          { $divide: ["$totalScores", "$totalInspections"] },
          2
        ]
      },
      totalRestaurants: {
        $size: {
          $reduce: {
            input: "$allRestaurantIds",
            initialValue: [],
            in: { $setUnion: ["$$value", "$$this"] }
          }
        }
      },
      gradeDistribution: {
        $sortArray: {
          input: "$gradeDistribution",
          sortBy: { grade: 1 }
        }
      }
    }
  },
  // Stage 6: Sort results by borough name
  {
    $sort: {
      borough: 1
    }
  }
])

