db.restaurants.aggregate([
  // Stage 1: Match Mexican restaurants in Queens or Brooklyn
  {
    $match: {
      cuisine: "Mexican",
      borough: { $in: ["Queens", "Brooklyn"] }
    }
  },
  // Stage 2: Filter restaurants with at least 4 inspections
  {
    $match: {
      $expr: { $gte: [{ $size: "$grades" }, 4] }
    }
  },
  // Stage 3: Sort grades by date and calculate averages
  {
    $addFields: {
      sortedGrades: {
        $sortArray: {
          input: "$grades",
          sortBy: { date: 1 }
        }
      }
    }
  },
  // Stage 4: Calculate early and recent average scores
  {
    $addFields: {
      inspectionCount: { $size: "$grades" },
      earlyAvgScore: {
        $avg: [
          { $arrayElemAt: ["$sortedGrades.score", 0] },
          { $arrayElemAt: ["$sortedGrades.score", 1] }
        ]
      },
      recentAvgScore: {
        $avg: [
          { $arrayElemAt: ["$sortedGrades.score", -1] },
          { $arrayElemAt: ["$sortedGrades.score", -2] }
        ]
      }
    }
  },
  // Stage 5: Calculate improvement (early - recent, since lower is better)
  {
    $addFields: {
      improvement: { $subtract: ["$earlyAvgScore", "$recentAvgScore"] }
    }
  },
  // Stage 6: Sort by improvement (highest improvement first)
  {
    $sort: { improvement: -1 }
  },
  // Stage 7: Limit to top 5
  {
    $limit: 5
  },
  // Stage 8: Project only required fields
  {
    $project: {
      name: 1,
      borough: 1,
      earlyAvgScore: 1,
      recentAvgScore: 1,
      improvement: 1,
      inspectionCount: 1,
      _id: 0
    }
  }
])

db.restaurants.aggregate([
  // Match restaurants in Queens
  { $match: { borough: "Queens" } },

  // Process grades array
  {
    $addFields: {
      // Filter grades from 2012 onwards and sort by date descending
      recentGrades: {
        $sortArray: {
          input: {
            $filter: {
              input: "$grades",
              cond: { $gte: ["$$this.date", new Date("2012-01-01")] }
            }
          },
          sortBy: { date: -1 }
        }
      }
    }
  },

  // Find consecutive A grades
  {
    $addFields: {
      consecutiveAGrades: {
        $reduce: {
          input: "$recentGrades",
          initialValue: { consecutive: [], current: [], maxConsecutive: 0 },
          in: {
            $cond: {
              if: { $eq: ["$$this.grade", "A"] },
              then: {
                consecutive: {
                  $cond: {
                    if: { $gt: [{ $size: { $concatArrays: ["$$value.current", ["$$this"]] } }, "$$value.maxConsecutive"] },
                    then: { $concatArrays: ["$$value.current", ["$$this"]] },
                    else: "$$value.consecutive"
                  }
                },
                current: { $concatArrays: ["$$value.current", ["$$this"]] },
                maxConsecutive: {
                  $max: ["$$value.maxConsecutive", { $size: { $concatArrays: ["$$value.current", ["$$this"]] } }]
                }
              },
              else: {
                consecutive: "$$value.consecutive",
                current: [],
                maxConsecutive: "$$value.maxConsecutive"
              }
            }
          }
        }
      }
    }
  },

  // Extract the consecutive A grades array
  {
    $addFields: {
      consecutiveAGrades: "$consecutiveAGrades.consecutive"
    }
  },

  // Filter restaurants with at least 3 consecutive A grades
  {
    $match: {
      $expr: { $gte: [{ $size: "$consecutiveAGrades" }, 3] }
    }
  },

  // Project required fields
  {
    $project: {
      name: 1,
      cuisine: 1,
      consecutiveAGrades: {
        $map: {
          input: "$consecutiveAGrades",
          as: "grade",
          in: {
            date: "$$grade.date",
            score: "$$grade.score"
          }
        }
      }
    }
  },

  // Limit to 10 results
  { $limit: 10 }
])

db.restaurants.aggregate([
  // First, add a field with only grades from 2011-2014
  {
    $addFields: {
      gradesInPeriod: {
        $filter: {
          input: "$grades",
          cond: {
            $and: [
              { $gte: ["$$this.date", new Date("2011-01-01")] },
              { $lte: ["$$this.date", new Date("2014-12-31")] }
            ]
          }
        }
      }
    }
  },
  // Filter restaurants with more than 4 inspections in the period
  {
    $match: {
      $expr: { $gt: [{ $size: "$gradesInPeriod" }, 4] }
    }
  },
  // Calculate average days between inspections and grade distribution
  {
    $addFields: {
      // Sort grades by date for calculating intervals
      sortedGrades: {
        $sortArray: {
          input: "$gradesInPeriod",
          sortBy: { date: 1 }
        }
      }
    }
  },
  {
    $addFields: {
      // Calculate days between consecutive inspections
      daysBetweenInspections: {
        $map: {
          input: { $range: [1, { $size: "$sortedGrades" }] },
          as: "idx",
          in: {
            $divide: [
              {
                $subtract: [
                  { $arrayElemAt: ["$sortedGrades.date", "$$idx"] },
                  { $arrayElemAt: ["$sortedGrades.date", { $subtract: ["$$idx", 1] }] }
                ]
              },
              86400000 // Convert milliseconds to days
            ]
          }
        }
      },
      // Count grade distribution
      gradeA: {
        $size: {
          $filter: {
            input: "$gradesInPeriod",
            cond: { $eq: ["$$this.grade", "A"] }
          }
        }
      },
      gradeB: {
        $size: {
          $filter: {
            input: "$gradesInPeriod",
            cond: { $eq: ["$$this.grade", "B"] }
          }
        }
      },
      gradeC: {
        $size: {
          $filter: {
            input: "$gradesInPeriod",
            cond: { $eq: ["$$this.grade", "C"] }
          }
        }
      }
    }
  },
  // Project final output
  {
    $project: {
      name: 1,
      cuisine: 1,
      avgDaysBetweenInspections: { $avg: "$daysBetweenInspections" },
      totalInspections: { $size: "$gradesInPeriod" },
      gradeDistribution: {
        A: "$gradeA",
        B: "$gradeB",
        C: "$gradeC"
      }
    }
  },
  // Sort by average days between inspections to see correlation patterns
  {
    $sort: { avgDaysBetweenInspections: 1 }
  },
  // Limit to 12 results
  {
    $limit: 12
  }
])

