db.restaurants.aggregate([
  // Match restaurants in Queens
  { $match: { borough: "Queens" } },

  // Ensure grades array exists and has at least 3 elements
  { $match: { 
    grades: { $exists: true },
    $expr: { $gte: [{ $size: "$grades" }, 3] }
  }},

  // Add a field to track consecutive score increases
  { $addFields: {
    consecutiveIncreases: {
      $let: {
        vars: {
          sortedGrades: { $sortArray: { input: "$grades", sortBy: { date: 1 } } }
        },
        in: {
          $reduce: {
            input: { $range: [1, { $size: "$$sortedGrades" }] },
            initialValue: {
              maxConsecutive: 0,
              currentConsecutive: 0,
              prevScore: { $arrayElemAt: ["$$sortedGrades.score", 0] }
            },
            in: {
              $let: {
                vars: {
                  currentScore: { $arrayElemAt: ["$$sortedGrades.score", "$$this"] }
                },
                in: {
                  maxConsecutive: {
                    $cond: {
                      if: { $gt: ["$$currentScore", "$$value.prevScore"] },
                      then: { $max: ["$$value.maxConsecutive", { $add: ["$$value.currentConsecutive", 1] }] },
                      else: "$$value.maxConsecutive"
                    }
                  },
                  currentConsecutive: {
                    $cond: {
                      if: { $gt: ["$$currentScore", "$$value.prevScore"] },
                      then: { $add: ["$$value.currentConsecutive", 1] },
                      else: 0
                    }
                  },
                  prevScore: "$$currentScore"
                }
              }
            }
          }
        }
      }
    }
  }},

  // Filter restaurants with at least 3 consecutive score increases
  { $match: { "consecutiveIncreases.maxConsecutive": { $gte: 2 } } },

  // Project the required fields
  { $project: {
    name: 1,
    cuisine: 1,
    address: {
      building: "$address.building",
      street: "$address.street",
      zipcode: "$address.zipcode"
    },
    grades: 1
  }},

  // Limit to 10 results
  { $limit: 10 }
])

