db.restaurants.aggregate([
  // Match restaurants in Queens and Brooklyn
  {
    $match: {
      borough: { $in: ["Queens", "Brooklyn"] }
    }
  },
  // Unwind the grades array to work with individual grades
  {
    $unwind: "$grades"
  },
  // Filter for inspections in 2014
  {
    $match: {
      "grades.date": {
        $gte: new Date("2014-01-01"),
        $lt: new Date("2015-01-01")
      }
    }
  },
  // Group by borough and grade to calculate statistics
  {
