        $push: {
          grade: "$_id.grade",
          averageScore: "$averageScore",
          minScore: "$minScore",
          maxScore: "$maxScore",
          restaurantCount: "$restaurantCount"
        }
      },
      totalCount: { $sum: "$restaurantCount" }
    }
