    }
  },
  {
    $addFields: {
      lastInspectionDate: { $max: "$grades.date" },
      has2025Inspection: {
        $gt: [
          {
            $size: {
              $filter: {
                input: "$grades",
                cond: {
                  $and: [
                    { $gte: ["$$this.date", new Date("2025-01-01")] },
                    { $lt: ["$$this.date", new Date("2026-01-01")] }
                  ]
                }
              }
            }
          },
          0
        ]
      }
    }
  },
  {
    $addFields: {
      category: {
        $cond: {
          if: "$has2025Inspection",
          then: "recentlyInspected",
          else: {
            $cond: {
              if: { $lt: ["$lastInspectionDate", new Date("2024-01-01")] },
              then: "notRecentlyInspected",
              else: "other"
            }
          }
        }
      }
    }
  },
  {
    $match: {
      category: { $in: ["recentlyInspected", "notRecentlyInspected"] }
    }
  },
  {
    $group: {
      _id: {
        zipcode: "$address.zipcode",
        category: "$category"
      },
      count: { $sum: 1 }
    }
  },
  {
    $group: {
      _id: "$_id.zipcode",
      categories: {
        $push: {
          category: "$_id.category",
          count: "$count"
        }
      }
    }
  },
  {
    $project: {
      zipcode: "$_id",
      recentlyInspected: {
        $ifNull: [
          {
            $arrayElemAt: [
              {
                $map: {
                  input: {
                    $filter: {
                      input: "$categories",
                      cond: { $eq: ["$$this.category", "recentlyInspected"] }
                    }
                  },
                  in: "$$this.count"
                }
              },
              0
            ]
          },
          0
        ]
      },
      notRecentlyInspected: {
        $ifNull: [
          {
            $arrayElemAt: [
              {
                $map: {
                  input: {
                    $filter: {
                      input: "$categories",
                      cond: { $eq: ["$$this.category", "notRecentlyInspected"] }
                    }
                  },
                  in: "$$this.count"
                }
              },
              0
            ]
          },
          0
        ]
      }
    }
  },
  {
    $addFields: {
      turnoverRate: {
        $round: [
          {
            $multiply: [
              {
                $divide: [
                  "$notRecentlyInspected",
                  { $add: ["$recentlyInspected", "$notRecentlyInspected"] }
                ]
              },
              100
            ]
          },
          2
        ]
      }
    }
  },
  {
    $sort: { zipcode: 1 }
  }
])

