  },
  // Calculate percentage change
  {
    $addFields: {
      percentageChange: {
        $cond: [
          { $and: [
            { $ne: ["$q1_avgScore", null] },
            { $ne: ["$q1_avgScore", 0] }
          ]},
          {
            $round: [
              {
                $multiply: [
                  {
                    $divide: [
                      { $subtract: ["$q2_avgScore", "$q1_avgScore"] },
                      "$q1_avgScore"
                    ]
                  },
                  100
                ]
              },
              2
            ]
          },
          null
        ]
      }
    }
  },
  // Use facet to create both restaurant details and overall metrics
  {
    $facet: {
      restaurants: [
        {
          $project: {
            name: 1,
            borough: 1,
            q2_avgScore: 1,
            q2_grade: 1,
            q1_avgScore: 1,
            q1_grade: 1,
            percentageChange: 1
          }
        }
      ],
      overallMetrics: [
        {
          $group: {
            _id: null,
            avgScoreAllLocations: { $avg: "$q2_avgScore" },
            allQ2Grades: { $push: "$q2_allGrades" }
          }
        },
        {
          $project: {
            avgScoreAllLocations: { $round: ["$avgScoreAllLocations", 2] },
            flatGrades: {
              $reduce: {
                input: "$allQ2Grades",
                initialValue: [],
                in: { $concatArrays: ["$$value", "$$this"] }
              }
            }
          }
        },
        {
          $unwind: "$flatGrades"
        },
        {
          $group: {
            _id: "$flatGrades",
            count: { $sum: 1 },
            avgScore: { $first: "$avgScoreAllLocations" }
          }
        },
        {
          $group: {
            _id: null,
            avgScoreAllLocations: { $first: "$avgScore" },
            gradeDistribution: {
              $push: {
                k: "$_id",
                v: "$count"
              }
            }
          }
        },
        {
          $project: {
            avgScoreAllLocations: 1,
            gradeDistribution: { $arrayToObject: "$gradeDistribution" }
          }
        }
      ]
    }
  },
  // Final formatting
  {
    $project: {
      quarter: "Q2 2025",
      restaurants: 1,
      overallMetrics: { $arrayElemAt: ["$overallMetrics", 0] }
    }
  }
])

db.restaurants.aggregate([
  // Filter for restaurants in Queens and Brooklyn
  {
    $match: {
      borough: { $in: ["Queens", "Brooklyn"] }
    }
  },
  // Calculate consecutive A grades
  {
    $addFields: {
      consecutiveAGrades: {
        $reduce: {
          input: "$grades",
          initialValue: { maxConsecutive: 0, currentConsecutive: 0 },
          in: {
            $cond: {
              if: { $eq: ["$$this.grade", "A"] },
              then: {
                maxConsecutive: {
                  $max: [
                    "$$value.maxConsecutive",
                    { $add: ["$$value.currentConsecutive", 1] }
                  ]
                },
                currentConsecutive: { $add: ["$$value.currentConsecutive", 1] }
              },
              else: {
                maxConsecutive: "$$value.maxConsecutive",
                currentConsecutive: 0
              }
            }
          }
        }
      }
    }
  },
  // Extract the maximum consecutive count
  {
    $addFields: {
      consecutiveAGrades: "$consecutiveAGrades.maxConsecutive"
    }
  },
  // Filter for restaurants with at least 4 consecutive A grades
  {
    $match: {
      consecutiveAGrades: { $gte: 4 }
    }
  },
  // Project only required fields
  {
    $project: {
      name: 1,
      borough: 1,
      cuisine: 1,
      consecutiveAGrades: 1
    }
  },
  // Limit to 15 results
  {
    $limit: 15
  }
])

db.restaurants.aggregate([
  // Stage 1: Filter for Latin restaurants in Brooklyn
  {
    $match: {
      borough: "Brooklyn",
      cuisine: "Latin (Cuban, Dominican, Puerto Rican, South & Central American)"
    }
  },

  // Stage 2: Calculate consecutive A grades and statistics
  {
    $addFields: {
      // Calculate consecutive A grades from the beginning
      consecutiveAGrades: {
        $reduce: {
          input: "$grades",
          initialValue: { count: 0, maxCount: 0, currentStreak: 0 },
          in: {
            $cond: {
              if: { $eq: ["$$this.grade", "A"] },
              then: {
                count: { $add: ["$$value.count", 1] },
                maxCount: { $max: [{ $add: ["$$value.currentStreak", 1] }, "$$value.maxCount"] },
                currentStreak: { $add: ["$$value.currentStreak", 1] }
              },
              else: {
                count: "$$value.count",
                maxCount: "$$value.maxCount",
                currentStreak: 0
              }
            }
          }
        }
      },
      // Calculate average score
      averageScore: { $avg: "$grades.score" },
      // Get last inspection date
      lastInspectionDate: { $arrayElemAt: ["$grades.date", 0] }
    }
  },

  // Stage 3: Extract the consecutive A grade count
  {
    $addFields: {
      consecutiveAGrades: "$consecutiveAGrades.maxCount"
    }
  },

  // Stage 4: Sort by consecutive A grades (descending)
  {
    $sort: { consecutiveAGrades: -1 }
  },

  // Stage 5: Limit to top 15
  {
    $limit: 15
  },

  // Stage 6: Project required fields
  {
    $project: {
      name: 1,
      address: {
        street: "$address.street",
        building: "$address.building"
      },
      consecutiveAGrades: 1,
      averageScore: { $round: ["$averageScore", 2] },
      lastInspectionDate: 1
    }
  }
])

db.restaurants.aggregate([
  // Filter for Brooklyn restaurants
  {
    $match: {
      borough: "Brooklyn"
    }
  },
  // Ensure restaurant has at least 3 inspections
  {
    $match: {
      $expr: { $gte: [{ $size: "$grades" }, 3] }
    }
  },
  // Add fields for analysis
  {
    $addFields: {
      // Get the last 3 grades
      last3Grades: { $slice: ["$grades", 0, 3] },
      // Get all grades except the last 3
      previousGrades: { $slice: ["$grades", 3, { $size: "$grades" }] }
    }
  },
  // Check if all last 3 grades are A
  {
    $match: {
      $expr: {
        $eq: [
          { $size: {
            $filter: {
              input: "$last3Grades",
              cond: { $eq: ["$$this.grade", "A"] }
            }
          }},
          3
        ]
      }
    }
  },
  // Check if there's at least one B or C grade before the A streak
  {
    $match: {
      $expr: {
        $gt: [
          { $size: {
            $filter: {
              input: "$previousGrades",
              cond: { $in: ["$$this.grade", ["B", "C"]] }
            }
          }},
          0
        ]
      }
    }
  },
  // Project the required fields
  {
    $project: {
      name: 1,
      cuisine: 1,
      // Get the most recent B or C grade before the A streak
      previousGrade: {
        $arrayElemAt: [
          {
            $map: {
              input: {
                $filter: {
                  input: "$previousGrades",
                  cond: { $in: ["$$this.grade", ["B", "C"]] }
                }
              },
              in: "$$this.grade"
            }
          },
          0
        ]
      },
      currentStreak: { $literal: 3 },
      latestScore: { $arrayElemAt: ["$last3Grades.score", 0] },
      // Date of the oldest A grade in the current streak (3rd most recent)
      improvementDate: { $arrayElemAt: ["$last3Grades.date", 2] }
    }
  },
  // Limit to 12 results
  {
    $limit: 12
  }
])

db.restaurants.aggregate([
  // Match Mexican restaurants
  {
    $match: {
      cuisine: "Mexican"
