db.restaurants.aggregate([
  // First, filter restaurants that have both A and B grades
  {
    $match: {
      "grades.grade": { $in: ["A", "B"] }
    }
  },
  // Calculate recovery times for B to A transitions
  {
    $addFields: {
      recoveryTimes: {
        $reduce: {
          input: { $range: [0, { $subtract: [{ $size: "$grades" }, 1] }] },
          initialValue: [],
          in: {
            $let: {
              vars: {
                currentGrade: { $arrayElemAt: ["$grades", "$$this"] },
                nextGrade: { $arrayElemAt: ["$grades", { $add: ["$$this", 1] }] }
              },
              in: {
                $cond: {
                  if: {
                    $and: [
                      { $eq: ["$$currentGrade.grade", "B"] },
                      { $eq: ["$$nextGrade.grade", "A"] }
                    ]
                  },
                  then: {
                    $concatArrays: [
                      "$$value",
                      [{
                        daysToRecover: {
                          $divide: [
                            { $subtract: ["$$nextGrade.date", "$$currentGrade.date"] },
                            86400000 // Convert milliseconds to days
                          ]
                        },
                        bGradeDate: "$$currentGrade.date",
                        aGradeDate: "$$nextGrade.date"
                      }]
                    ]
                  },
                  else: "$$value"
                }
              }
            }
          }
        }
      }
    }
  },
  // Filter out restaurants with no B to A recoveries
  {
    $match: {
      recoveryTimes: { $ne: [] }
    }
  },
  // Unwind the recovery times to get one document per recovery
  {
    $unwind: "$recoveryTimes"
  },
  // Sort by recovery time (fastest first)
  {
    $sort: { "recoveryTimes.daysToRecover": 1 }
  },
  // Limit to top 10
  {
    $limit: 10
  },
  // Project final result
  {
    $project: {
      name: 1,
      daysToRecover: "$recoveryTimes.daysToRecover"
    }
  }
])

db.restaurants.aggregate([
  // Unwind grades array to work with individual grades
  { $unwind: "$grades" },

  // Sort by restaurant and date to ensure chronological order
  { $sort: { "_id": 1, "grades.date": 1 } },

  // Group back by restaurant to get ordered grades array
  {
    $group: {
      _id: "$_id",
      borough: { $first: "$borough" },
      name: { $first: "$name" },
      grades: { $push: "$grades" }
    }
  },

  // Process each restaurant to find C->A recoveries
  {
    $project: {
      borough: 1,
      name: 1,
      recoveries: {
        $reduce: {
          input: { $range: [1, { $size: "$grades" }] },
          initialValue: [],
          in: {
            $cond: [
              // Check if previous grade is C and current grade is A
              {
                $and: [
                  { $eq: [{ $arrayElemAt: ["$grades.grade", { $subtract: ["$$this", 1] }] }, "C"] },
                  { $eq: [{ $arrayElemAt: ["$grades.grade", "$$this"] }, "A"] }
                ]
              },
              // If C->A recovery found, add the recovery time in milliseconds
              {
                $concatArrays: [
                  "$$value",
                  [{
                    recoveryTimeMs: {
                      $subtract: [
                        { $arrayElemAt: ["$grades.date", "$$this"] },
                        { $arrayElemAt: ["$grades.date", { $subtract: ["$$this", 1] }] }
                      ]
                    }
                  }]
                ]
              },
              "$$value"
            ]
          }
        }
      }
    }
  },

  // Filter out restaurants with no recoveries
  { $match: { "recoveries.0": { $exists: true } } },

  // Unwind recoveries to calculate averages
  { $unwind: "$recoveries" },

  // Convert recovery time from milliseconds to days
  {
    $project: {
      borough: 1,
      recoveryDays: { $divide: ["$recoveries.recoveryTimeMs", 1000 * 60 * 60 * 24] }
    }
  },

  // Group by borough to calculate averages
  {
    $group: {
      _id: "$borough",
      avgRecoveryDays: { $avg: "$recoveryDays" },
      recoveryCount: { $sum: 1 }
    }
  },

  // Filter boroughs with at least 5 recoveries
  { $match: { recoveryCount: { $gte: 5 } } },

  // Format output
  {
    $project: {
      borough: "$_id",
      avgRecoveryDays: { $round: ["$avgRecoveryDays", 2] },
      recoveryCount: 1
    }
  },

  // Sort by average recovery days
  { $sort: { avgRecoveryDays: 1 } }
])

